    <script>
        function downloadAlert () {
            
            setTimeout(() => {
                alert('Download Completed')
            }, 3000);
        }

        /* === CONTACT FORM === */
        const scriptURL = 'https://script.google.com/macros/s/AKfycbxe36XHaJ7IMXLZx9dyPYIMxBRz6QUHM4LseR7R0vZiNaBm6TE8m9Ame6LaSkl0q4lpNA/exec'
        const form = document.forms['portofolio-contact-form']

        // get elements
        const btnSubmit = document.querySelector('.btn-submit')
        const btnSubmitLoading = document.querySelector('.btn-submit-loading')
        const alert = document.getElementById('alert-3')

        // btnSubmit.addEventListener('click', e => {
        //     btnSubmitLoading.classList.toggle('hidden')
        // })

        form.addEventListener('submit', e => {
            e.preventDefault()

            // munculkan btn loading & hilangkan btn submit
            btnSubmit.classList.toggle('hidden')
            btnSubmitLoading.classList.toggle('hidden')

            fetch(scriptURL, { method: 'POST', body: new FormData(form)})
            .then((response) => {
                // munculkan btn submit & hilangkan btn loading
                btnSubmit.classList.toggle('hidden')
                btnSubmitLoading.classList.toggle('hidden')

                // munculkan alert
                alert.classList.replace('hidden', 'flex')

                // reset form
                form.reset()
            })
            .catch(error => console.error('Error!', error.message))
        })
    </script>
    
